import React, { useState, useEffect } from 'react';

const Players = ({ teamdata }) => {

  const rows = teamdata.players.map((player, index) => {

    let matches = 0;
    let practises = 0;
    let scores = 0;
    let assists = 0;
    for (let i = 0; i < teamdata.matches.length; i++) {
      (teamdata.matches[i].participants.includes(player.id)) ? matches++ : null;
    }

    return (
      <tr key={"row" + index}>
        <td>{player.lname + " " + player.fname}</td>
        <td>{player.number}</td>
        <td>{player.role}</td>
        <td>{matches}</td>
        <td>{practises}</td>
        <td>{scores}</td>
        <td>{assists}</td>
      </tr>
    );
  });

  return (
    <table className="Players">
      <thead>
        <tr>
          <th>Nimi</th>
          <th>Pelinumero</th>
          <th>Rooli</th>
          <th>Pelit</th>
          <th>Harjoitukset</th>
          <th>Maalit</th>
          <th>Syötöt</th>
        </tr>
      </thead>
      <tbody>
        {rows}
      </tbody>
    </table>
  );
}

export default Players;
