import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

const App = () => {
  const [content, setContent] = useState("main");
  const [teamdata, setTeamData] = useState([]);

  const handleContent = (event) => {
    setContent(event.target.id);
  }

  useEffect ( () => {
    axios.get('teamdata.json')
    .then(response => {
      setTeamData(response.data);
    })
    .catch(error => {
      console.log(error);
    });
  }, []);

  return (
    <article className="App">
      <Header />
      <Navigation content={content} changeContent={handleContent} />
      <Content content={content} data={teamdata}/>
    </article>
  );
}

const Header = () => {
  const teamname = "Huutoniemen Hoki";
  const shortname = "HH";
  return (
    <header className="App-header">
      <p>{shortname}</p>
      <h1>{teamname}</h1>
    </header>
  );
}

const Navigation = ({ content, changeContent }) => {
  const links = [
    { "id": "main", "title": "Pääsivu" },
    { "id": "matches", "title": "Ottelut" },
    { "id": "practises", "title": "Harjoitukset" },
    { "id": "players", "title": "Pelaajat" },
    { "id": "stats", "title": "Tilastot" }
  ];

  const lilist = links.map((links) => {
    if (links.id === content) {
      return <li
        key={links.id}
        id={links.id}
        onClick={changeContent}
        className="active"
      >{links.title}
      </li>;
    }
    else {
      return <li
        key={links.id}
        id={links.id}
        onClick={changeContent}
      >{links.title}
      </li>;
    }

  });
  return (
    <nav className="App-nav">
      <ul>
        {lilist}
      </ul>
    </nav>
  );
}

const Content = ({ content, data }) => {
  let selected_content;
  if (content === "main") selected_content = <Main teamdata={data}/>;
  if (content === "matches") selected_content = <Matches teamdata={data}/>;
  if (content === "practises") selected_content = <Practises teamdata={data}/>;
  if (content === "players") selected_content = <Players teamdata={data}/>;
  if (content === "stats") selected_content = <Stats teamdata={data}/>;

  return (
    <section className="App-content">
      {selected_content}
    </section>
  );
}

const Main = ({ teamdata }) => {
  return (
    <article>
      <p>Tervetuloa Huutoniemen Hoki -mielikuvitusjoukkueen kotisivulle!</p>
      <p>Täältä löydät tietoa jääkiekkojoukkueen pelatuista peleistä, harjoituksista, pelaajista ja tilastoista.</p>
    </article>
  );
}

const Matches = ({ teamdata }) => {
  return (
    <p>{teamdata}</p>
  );
}

const Practises = ({ teamdata }) => {
  return (
    <p>{teamdata}</p>
  );
}

const Players = ({ teamdata }) => {
  return (
    <p>{teamdata}</p>
  );
}

const Stats = ({ teamdata }) => {
  return (
    <p>{teamdata}</p>
  );
}

export default App;
