import React, { useState, useEffect } from 'react';
import './App.css';
//import $ from 'jquery';

const App = () => {
  const [content, setContent] = useState("paasivu");
  const handleContent = (event) => {
    setContent(event.target.id);
  };

  return (
    <article className="App">
      <Header />
      <Navigation content={content} changeContent={handleContent} />
    </article>
  );
}

const Header = () => {
  const joukkue = "Huutoniemen Hoki";
  const lyhenne = "HH";
  return (
    <header className="App-header">
      <p>{lyhenne}</p>
      <h1>{joukkue}</h1>
    </header>
  );
}

const Navigation = ({ content, changeContent }) => {
  console.log(content);
  const links = [
    { "id": "paasivu", "title": "P채채sivu" },
    { "id": "ottelut", "title": "Ottelut" },
    { "id": "harjoitukset", "title": "Harjoitukset" },
    { "id": "pelaajat", "title": "Pelaajat" },
    { "id": "tilastot", "title": "Tilastot" }
  ];

  //links.map((name, index)=> console.log(index,name));
  const lilist = links.map((links) => {
    if (links.id == content) {
      <li id={links.id} classList="active">{links.title}</li>
    }
    else {
      <li id={links.id} >{links.title}</li>
    };

  });
  return (
    <nav className="App-nav">
      <ul>
        {lilist}
      </ul>
    </nav>
  );
}

export default App;
