import React, { useState, useEffect } from 'react';
import './App.css';
//import $ from 'jquery';

const App = () => {
  const [content, setContent] = useState("main");

  const handleContent = (event) => {
    setContent(event.target.id);
  }

  $.ajax({
    url: 'react11-99.json',
    cache: false,
    dataType: 'json'
  }).done((data) => {
    this.setState({ persons: data.persons, loaded: true });
  }).fail((jqXHR, textStatus, errorThrown) => {
    console.log(textStatus + ":" + errorThrown);
  });


  return (
    <article className="App">
      <Header />
      <Navigation content={content} changeContent={handleContent} />
      <Content content={content} />
    </article>
  );
}

const Header = () => {
  const teamname = "Huutoniemen Hoki";
  const shortname = "HH";
  return (
    <header className="App-header">
      <p>{shortname}</p>
      <h1>{teamname}</h1>
    </header>
  );
}

const Navigation = ({ content, changeContent }) => {
  const links = [
    { "id": "main", "title": "Pääsivu" },
    { "id": "matches", "title": "Ottelut" },
    { "id": "practises", "title": "Harjoitukset" },
    { "id": "players", "title": "Pelaajat" },
    { "id": "stats", "title": "Tilastot" }
  ];

  const lilist = links.map((links) => {
    if (links.id === content) {
      return <li
        key={links.id}
        id={links.id}
        onClick={changeContent}
        className="active"
      >{links.title}
      </li>;
    }
    else {
      return <li
        key={links.id}
        id={links.id}
        onClick={changeContent}
      >{links.title}
      </li>;
    }

  });
  return (
    <nav className="App-nav">
      <ul>
        {lilist}
      </ul>
    </nav>
  );
}

const Content = ({ content }) => {
  let selected_content;
  if (content === "main") selected_content = <Main />;
  if (content === "matches") selected_content = <Matches />;
  if (content === "practises") selected_content = <Practises />;
  if (content === "players") selected_content = <Players />;
  if (content === "stats") selected_content = <Stats />;

  return (
    <section className="App-content">
      {selected_content}
    </section>
  );
}

const Main = ({ content }) => {
  return (
    <article>
      <p>Tervetuloa Huutoniemen Hoki -mielikuvitusjoukkueen kotisivulle!</p>
      <p>Täältä löydät tietoa jääkiekkojoukkueen pelatuista peleistä, harjoituksista, pelaajista ja tilastoista.</p>
    </article>
  );
}

const Matches = ({ content }) => {
  return (
    <p>Matches</p>
  );
}

const Practises = ({ content }) => {
  return (
    <p>Practises</p>
  );
}

const Players = ({ content }) => {
  return (
    <p>Players</p>
  );
}

const Stats = ({ content }) => {
  return (
    <p>Stats</p>
  );
}

export default App;
