import React, { useState } from 'react';
import { HorizontalBar } from 'react-chartjs-2';

const Stats = ({ teamdata }) => {
  const [firstload, setFirstload] = useState(true);
  const [chartdata, setChartdata] = useState({});
  const [showroles, setShowroles] = useState("all");

  const barOptions = {
    responsive: true,
    maintainAspectRatio: false,
    tooltips: {
      titleFontSize: 20,
      bodyFontSize: 20
    },
    title: {
      display: true,
      text: 'Tehopisteet all-time top 10',
      fontSize: 24
    },
    legend: {
      display: false
    },
    scales: {
      yAxes: [{
        ticks: {
          fontSize: 20
        }
      }],
      xAxes: [{
        ticks: {
          beginAtZero: true,
          fontSize: 20
        }
      }]
    }
  };

  const selectionHandler = (event) => {
    console.log(event.target.value);
    setShowroles(event.target.value);
  }

  const showPoints = () => {
    let show = showroles;
    console.log(showroles);
    let players = {};
    players = teamdata.players.map((player, index) => {

      if (showroles === "forwards" && player.role !== "hyökkääjä") {
        return null;
      };
      if (showroles === "defenders" && player.role !== "puolustaja") {
        return null;
      };

      let points = 0;
      for (let i = 0; i < teamdata.matches.length; i++) {
        for (let k = 0; k < teamdata.matches[i].scorers.length; k++) {
          if (teamdata.matches[i].scorers[k] === player.id) {
            points++;
          }
          if (teamdata.matches[i].assists[k] === player.id) {
            points++;
          }
        }
      }
      return ({
        name: player.lname + " " + player.fname[0] + ".",
        points: points
      });
    });

    players = players.filter((e) => e != null);
    console.log(players);
    players.sort((a, b) => (a.points < b.points) ? 1 : (a.points > b.points) ? -1 : 0);

    let chartlabels = players.map((player) => player.name);
    let chartdata = players.map((player) => player.points);
    chartlabels = chartlabels.slice(0, 10);
    chartdata = chartdata.slice(0, 10);
    let data = {
      labels: chartlabels,
      datasets: [{
        label: "Tehopisteet",
        backgroundColor: 'green',
        borderColor: 'green',
        data: chartdata,
      }]
    };
    setChartdata(data);
  }

  if (firstload) {
    showPoints();
    setFirstload(false);
  };

  return (<div id="chartContainer">
    Valitse näytettävät roolit: 
    <select id="roles" value={showroles} onChange={selectionHandler}>
      <option key="all" value="all">Kaikki</option>
      <option key= "forwards" value="forwards">Hyökkääjät</option>
      <option key="defenders" value="defenders">Puolustajat</option>
    </select>
    <HorizontalBar data={chartdata} options={barOptions} />
  </div>
  );
}

export default Stats;
