import React, { useState } from 'react';
import { HorizontalBar } from 'react-chartjs-2';

const Stats = ({ teamdata }) => {
  const [chartdata, setChartdata] = useState({});
  const [firstload, setFirstload] = useState(true);
  const barOptions = {
    responsive: true,
    maintainAspectRatio: false,
    title: {
      display: true,
      text: 'Tehopisteet all-time top 10',
      fontSize: 24
    },
    legend: {
      display: false
    },
    scales: {
      yAxes: [{
        ticks: {
          fontSize: 16
        }
      }],
      xAxes: [{
        ticks: {
          beginAtZero: true,
          fontSize: 20
        }
      }]
    }
  };
  const showPoints = () => {
    const players = teamdata.players.map((player, index) => {
      let points = 0;
      for (let i = 0; i < teamdata.matches.length; i++) {
        for (let k = 0; k < teamdata.matches[i].scorers.length; k++) {
          if (teamdata.matches[i].scorers[k] === player.id) {
            points++;
          }
          if (teamdata.matches[i].assists[k] === player.id) {
            points++;
          }
        }
      }
      return ({
        name: player.lname + " " + player.fname[0] + ".",
        points: points
      });
    });
    players.sort((a, b) => (a.points < b.points) ? 1 : (a.points > b.points) ? -1 : 0);

    let chartlabels = players.map((player) => player.name);
    let chartdata = players.map((player) => player.points);
    chartlabels = chartlabels.slice(0,10);
    chartdata = chartdata.slice(0,10);
    let data = {
      labels: chartlabels,
      datasets: [{
        label: "Tehopisteet",
        backgroundColor: 'green',
        borderColor: 'green',
        data: chartdata,
      }]
    };
    setChartdata(data);
    //document.getElementById("myCheck").checked = true;
  }
  if (firstload) {
    showPoints();
    setFirstload(false);
  };

  return (<div id="chartContainer">
    <HorizontalBar data={chartdata} options={barOptions} />
  </div>
  );
}

export default Stats;
