import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';

const Stats = ({ teamdata }) => {
  const [chartdata, setChartdata] = useState({});
  const [firstload, setFirstload] = useState(true);

  const showPoints = () => {
    const players = teamdata.players.map((player, index) => {
      let points = 0;
      for (let i = 0; i < teamdata.matches.length; i++) {
        for (let k = 0; k < teamdata.matches[i].scorers.length; k++) {
          if (teamdata.matches[i].scorers[k] === player.id) {
            points++;
          }
          if (teamdata.matches[i].assists[k] === player.id) {
          points++;
        }
        }
      }
      return ({
        name : player.lname +" "+player.fname[0]+".",
        points : points
      });
    });
    players.sort((a,b)=> (a.points<b.points)?1:(a.points>b.points)?-1:0);
    console.log(players[0].points);
    const chartlabels = players.map((player)=>player.name);
    const chartdata = players.map((player)=>player.points);
    let data = {
      labels: chartlabels,
      datasets: [{
        label: "Tehopisteet all-time",
        backgroundColor: 'rgb(255, 99, 132)',
        borderColor: 'rgb(255, 99, 132)',
        data: chartdata,
      }]
    };
    setChartdata(data);
    //document.getElementById("myCheck").checked = true;
  }
  if (firstload) {
    showPoints();
    setFirstload(false);
  };

  return (<div id="chartContainer">
    <Bar data={chartdata} options={{ responsive: true, maintainAspectRatio: false }}/>
    </div>
  );
}

export default Stats;
