import React, { useState, useEffect } from 'react';

const Players = ({ teamdata }) => {
  const [userinput, setUserinput] = useState("");

  const findPlayer = event => {
    const input = event.target.value.toLower();
    setUserinput(input);
  }

  const rows = teamdata.players.map((player, index) => {
    const playername = player.lname + " " + player.fname;
    if (!playername.startsWith(userinput)){ return null}

    let matches = 0;
    for (let i = 0; i < teamdata.matches.length; i++) {
      if (teamdata.matches[i].participants.includes(player.id)){
      matches++;
      }
    }

    let practises = 0;
    for (let i = 0; i < teamdata.practises.length; i++) {
      if (teamdata.practises[i].participants.includes(player.id)){
        practises++;
      }
    }

    let scores = 0;
    for (let i = 0; i < teamdata.matches.length; i++) {
      if (teamdata.matches[i].scorers.includes(player.id)){
        scores++;
      }
    }

    let assists = 0;
    for (let i = 0; i < teamdata.matches.length; i++) {
      if (teamdata.matches[i].assists.includes(player.id)){
      assists++;
      }
    }

    return (
      <tr key={"row" + index}>
        <td>{player.lname + " " + player.fname}</td>
        <td>{player.number}</td>
        <td>{player.role}</td>
        <td>{matches}</td>
        <td>{practises}</td>
        <td>{scores}</td>
        <td>{assists}</td>
      </tr>
    );
  });

  return (
    <div className="Players">
    Etsi pelaaja: <input type="text" onChange={findPlayer}/>
    <table>
      <thead>
        <tr>
          <th>Nimi</th>
          <th>Pelinumero</th>
          <th>Rooli</th>
          <th>Pelit</th>
          <th>Harjoitukset</th>
          <th>Maalit</th>
          <th>Syötöt</th>
        </tr>
      </thead>
      <tbody>
        {rows}
      </tbody>
    </table>
    </div>
  );
}

export default Players;
