import React, { useState, useEffect } from 'react';
import './App.css';
//import $ from 'jquery';

const App = () => {
  const [content, setContent] = useState ( "paasivu" );
  const handleContent = (event) => {
    setContent(event.target.id);
  };

  return (
    <article className="App">
      <Header/>
      <Navigation content={content} changeContent={handleContent}/>
    </article>
  );
}

const Header = () => {
  const joukkue = "Huutoniemen Hoki";
  const lyhenne = "HH";
  return (
      <header className="App-header">
        <p>{lyhenne}</p>
        <h1>{joukkue}</h1>
      </header>
  );
}

const Navigation = ({content, changeContent}) => {
  console.log(content);

  return (
      <nav className="App-nav">
        <ul>
          <li id="paasivu" onClick={changeContent}>Pääsivu</li>
          <li id="ottelut" onClick={changeContent}>Ottelut</li>
          <li id="harjoitukset" onClick={changeContent}>Harjoitukset</li>
          <li id="pelaajat" onClick={changeContent}>Pelaajat</li>
          <li id="tilastot" onClick={changeContent}>Tilastot</li>
        </ul>
      </nav>
  );
}

export default App;
